style:
*::after,
*::before,
* {
	padding: 0;
	margin: 0;
	box-sizing: border-box;
}

.gallery-card {
	width: 400px;
	aspect-ratio: 1;
	border-radius: 20px;
	font-family: Arial, sans-serif;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	transition: box-shadow 0.3s ease;
	position: relative;
	overflow: hidden;
	cursor: pointer;
}

.gallery-card:hover {
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

.gallery-card .img-container {
	width: 100%;
	height: 100%;
	overflow: hidden;
	position: absolute;
	filter: grayscale(1);
	transition: all 0.4s ease;
}

.gallery-card .img-container img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.gallery-card .card-content {
	position: absolute;
	height: 100%;
	width: 100%;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 50px;
	color: whitesmoke;
	opacity: 0;
	transition: opacity 0.4s ease;
}

.gallery-card:hover .card-content {
	opacity: 1;
}

.gallery-card:hover .img-container {
	filter: grayscale(0);
}
