style:
*::after,
*::before,
* {
	padding: 0;
	margin: 0;
	box-sizing: border-box;
}

.gallery-card {
	margin: 50px;
	width: 400px;
	aspect-ratio: 1;
	border-radius: 20px;
	font-family: Arial, sans-serif;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	transition: box-shadow 0.3s ease;
	overflow: hidden;
	cursor: pointer;
	display: flex;
	flex-direction: column;
	background-color: whitesmoke;
}

.gallery-card:hover {
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

.gallery-card figure {
	width: 100%;
	height: 100%;
}

.gallery-card figure img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: translate 0.4s ease;
}

.gallery-card figcaption {
	display: block;
	text-align: center;
	transition: translate 0.4s ease;
	padding: 0 0 5px;
	font-weight: bolder;
}

.gallery-card:hover figcaption {
	translate: 0 -110%;
}

.gallery-card:hover img {
	translate: 0 -1.5rem;
}
