style:
@import url(https://fonts.googleapis.com/css?family=Roboto);

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

.card-container {
	margin: 50px;
	position: relative;
	cursor: pointer;
	width: 300px;
}

.card-content {
	width: 300px;
	height: 200px;
	transition: 0.5s;
}

.card-top {
	position: relative;
	background: #2c3a47;
	display: flex;
	justify-content: center;
	align-items: center;
	z-index: 1;
	transform: translateY(100px);
}

.card-top img {
	position: absolute;
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: all 0.3s ease;
	filter: blur(4px);
}

.card-top h3 {
	position: absolute;
	z-index: 1;
	color: white;
	font-size: 30px;
	letter-spacing: 1px;
	text-decoration: underline;
}

.card-bottom {
	padding: 24px;
	position: absolute;
	background: #2c3a47;
	display: flex;
	flex-direction: column;
	justify-content: space-around;
	align-items: center;
	transform: translateY(-100px);
	color: #f5f5f5;
	text-align: center;
	box-shadow: 1px 2px 3px 6px rgba(0, 0, 0, 1);
	-webkit-box-shadow: 1px 2px 3px 6px rgba(0, 0, 0, 1);
	-moz-box-shadow: 1px 2px 3px 6px rgba(0, 0, 0, 1);
}

.card-container:hover .card-content {
	background: #2c3a47;
	transform: translateY(0);
}

.card-container:hover .card-bottom {
	box-shadow: 0px 2px 49px 13px rgba(0, 0, 0, 0.73);
	-webkit-box-shadow: 0px 2px 49px 13px rgba(0, 0, 0, 0.73);
	-moz-box-shadow: 0px 2px 49px 13px rgba(0, 0, 0, 0.73);
}

.card-bottom span {
	padding: 5px 10px;
	background-color: #f5f5f5;
	border-radius: 5px;
}

.card-bottom span:hover {
	background-color: gray;
}
.card-bottom span a {
	text-decoration: none;
	font-weight: 600;
	color: rgb(19, 18, 18);
}

.card-bottom span:hover a {
	color: #f5f5f5;
}

.card-container:hover .card-top img {
	filter: blur(0);
}
