style:
html {
  height: 100%;
}

body {
  height: 100%;
  margin: 0;
  background: linear-gradient(60deg, #da374c, #df6813);
  background-repeat: no-repeat;
  background-attachment: fixed;
  font-family: "Times New Roman", Times, serif;
}

.container {
  display: grid;
  justify-content: center;
  align-items: center;
  margin-top: 15%;
  text-align: center;
}

.cards-container {
  display: flex;
  justify-content: center;
  align-items: center;
}

.card {
  position: relative;
  height: 400px;
  aspect-ratio: 1 / 1.5;
  text-align: center;
  transition: 1s ease-in-out;
  transform-style: preserve-3d;
}

.basic {
  background-color: #fce4ec;
}

.standard {
  background-color: #b2dfdb;
}

.premium {
  background-color: #ffcc80;
}

.card:hover {
  transform: rotateY(180deg);
}

.card .back-card-body {
  transform: rotateY(180deg);
}

.card h2 {
  text-transform: uppercase;
  margin-top: 30%;
}

.card .front-card-body h3 {
  font-size: 40px;
  font-family: Georgia, "Times New Roman", Times, serif;
  color: rgba(36, 35, 35, 0.685);
  font-weight: 300;
}

.card .front-card-body p {
  font-size: 40px;
  font-family: Arial, Helvetica, sans-serif;
  font-weight: 600;
  color: rgba(36, 35, 35, 0.685);
  margin-top: -30px;
}

.card .back-card-body h3 {
  font-size: 25px;
  font-family: Georgia, "Times New Roman", Times, serif;
  color: rgba(36, 35, 35, 0.685);
}

.back-card-body,
.front-card-body {
  position: absolute;
  width: 100%;
  height: 100%;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
}

.card.basic:hover .back-card-body {
  transition: 1s ease-in-out;
}

.card.standard:hover {
  background-color: #c8e6c9;
}

.card.premium:hover {
  background-color: #ffb74d;
}

ul {
  list-style: none;
  padding: 0;
  margin: 0;
  display: inline;
  align-items: center;
  justify-content: center;
}

.details li {
  max-height: 100px;
  text-overflow: ellipsis;
  margin: 10px;
  padding: 5px;
  border-radius: 20px;
}

@media (max-width: 768px) {
  .card {
    width: 200px;
    height: 320px;
  }
  .card h2 {
    font-size: 20px;
  }
  .card h3 {
    font-size: 16px;
  }
}

@media (max-width: 480px) {
  .card {
    width: 180px;
    height: 290px;
  }
  .card h2 {
    font-size: 18px;
  }
  .card h3 {
    font-size: 14px;
  }
}
