style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Amazon Ember", Arial, sans-serif;
}

body {
	background: #f6f6f6;
	padding: 4rem 2rem;
	min-height: 100vh;
}

.aws-header {
	text-align: center;
	margin-bottom: 3rem;
}

.aws-logo {
	width: 100px;
	margin-bottom: 1rem;
}

.pricing-container {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
}

.aws-card {
	background: white;
	border-radius: 4px;
	padding: 2rem;
	border-top: 4px solid #ff9900;
	box-shadow: 0 1px 4px rgba(0, 0, 0, 0.15);
	transition: transform 0.2s ease;
}

.aws-card:hover {
	transform: translateY(-5px);
}

.enterprise-card {
	border-color: #232f3e;
}

.plan-tier {
	color: #ff9900;
	font-size: 1.2rem;
	font-weight: 600;
	margin-bottom: 1rem;
}

.price {
	font-size: 2.5rem;
	color: #232f3e;
	margin: 1rem 0;
}

.price small {
	font-size: 1rem;
	color: #666;
}

.aws-features {
	list-style: none;
	margin: 2rem 0;
}

.aws-features li {
	padding: 0.8rem 0;
	border-bottom: 1px solid #eee;
	display: flex;
	align-items: center;
}

.aws-features li:last-child {
	border-bottom: none;
}

.feature-icon {
	width: 20px;
	height: 20px;
	margin-right: 1rem;
	color: #ff9900;
}

.aws-cta {
	display: block;
	width: 100%;
	padding: 1rem;
	background: #ff9900;
	color: white;
	text-align: center;
	text-decoration: none;
	border-radius: 4px;
	font-weight: 600;
	transition: background 0.2s ease;
}

.aws-cta:hover {
	background: #eb8c00;
}

.enterprise-cta {
	background: #232f3e;
}

.enterprise-cta:hover {
	background: #1a2533;
}

.tooltip {
	position: relative;
	display: inline-block;
	border-bottom: 1px dotted #666;
	cursor: help;
}

.tooltip-text {
	visibility: hidden;
	width: 200px;
	background: #232f3e;
	color: #fff;
	text-align: center;
	padding: 0.5rem;
	border-radius: 4px;
	position: absolute;
	z-index: 1;
	bottom: 125%;
	left: 50%;
	margin-left: -100px;
}

.tooltip:hover .tooltip-text {
	visibility: visible;
}

.disclaimer {
	text-align: center;
	color: #666;
	margin-top: 2rem;
	font-size: 0.9rem;
}

script:
// Add tooltip functionality
const tooltips = document.querySelectorAll(".tooltip");
tooltips.forEach((tooltip) => {
	tooltip.addEventListener("mouseover", function () {
		this.querySelector(".tooltip-text").style.visibility = "visible";
	});
	tooltip.addEventListener("mouseout", function () {
		this.querySelector(".tooltip-text").style.visibility = "hidden";
	});
});
