style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Circular", -apple-system, BlinkMacSystemFont, sans-serif;
}

body {
	background: #000;
	color: #fff;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 4rem 2rem;
}

.spotify-header {
	text-align: center;
	margin-bottom: 3rem;
}

.spotify-logo {
	width: 120px;
	margin-bottom: 1.5rem;
}

.pricing-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
	max-width: 1200px;
	width: 100%;
}

.plan-card {
	background: #181818;
	border-radius: 10px;
	padding: 2rem;
	transition: transform 0.3s ease;
}

.plan-card:hover {
	transform: scale(1.03);
}

.popular-plan {
	border: 2px solid #1db954;
	position: relative;
	overflow: hidden;
}

.popular-badge {
	background: #1db954;
	color: #000;
	padding: 0.5rem 1rem;
	position: absolute;
	top: 0;
	right: 0;
	font-size: 0.9rem;
	font-weight: bold;
	border-radius: 0 0 0 10px;
}

.plan-name {
	color: #fff;
	font-size: 1.8rem;
	margin-bottom: 1rem;
}

.price {
	font-size: 2.5rem;
	margin: 1.5rem 0;
}

.price span {
	font-size: 1rem;
	color: #b3b3b3;
}

.features-list {
	list-style: none;
	margin: 2rem 0;
}

.features-list li {
	padding: 1rem 0;
	border-bottom: 1px solid #282828;
	display: flex;
	align-items: center;
}

.features-list li:last-child {
	border-bottom: none;
}

.spotify-icon {
	color: #1db954;
	margin-right: 1rem;
	font-size: 1.2rem;
}

.cta-button {
	width: 100%;
	padding: 1rem;
	background: #1db954;
	color: #000;
	border: none;
	border-radius: 500px;
	font-size: 1rem;
	font-weight: bold;
	cursor: pointer;
	transition: transform 0.2s ease;
}

.cta-button:hover {
	transform: scale(1.05);
}

.student-note {
	color: #b3b3b3;
	font-size: 0.9rem;
	margin-top: 1rem;
}

.toggle-container {
	display: flex;
	gap: 1rem;
	justify-content: center;
	margin-bottom: 2rem;
}

.toggle-button {
	color: #b3b3b3;
	cursor: pointer;
	padding: 0.5rem 1rem;
	border-radius: 20px;
}

.toggle-button.active {
	background: #535353;
	color: #fff;
}

@media (max-width: 768px) {
	.pricing-container {
		grid-template-columns: 1fr;
	}
}

script:
// Toggle functionality
const toggleButtons = document.querySelectorAll(".toggle-button");
toggleButtons.forEach((button) => {
	button.addEventListener("click", () => {
		toggleButtons.forEach((btn) => btn.classList.remove("active"));
		button.classList.add("active");
	});
});
