style:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', sans-serif;
}

body {
	background: linear-gradient(135deg, #0f172a, #1e293b);
	min-height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 4rem 2rem;
	color: white;
}

.pricing-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2.5rem;
	max-width: 1200px;
	width: 100%;
	position: relative;
}

.pricing-card {
	background: rgba(255, 255, 255, 0.05);
	border-radius: 20px;
	padding: 2.5rem;
	backdrop-filter: blur(12px);
	border: 1px solid rgba(255, 255, 255, 0.1);
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.pricing-card::before {
	content: '';
	position: absolute;
	top: -50%;
	left: -50%;
	width: 200%;
	height: 200%;
	background: conic-gradient(transparent, #818cf8, transparent 30%);
	animation: rotate 6s linear infinite;
}

.pricing-card::after {
	content: '';
	position: absolute;
	inset: 2px;
	border-radius: 18px;
	background: linear-gradient(152deg, #1e293b 0%, #0f172a 100%);
}

.pricing-card:hover {
	transform: translateY(-10px);
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.card-content {
	position: relative;
	z-index: 1;
}

.plan-name {
	font-size: 1.8rem;
	font-weight: 700;
	margin-bottom: 1rem;
	background: linear-gradient(45deg, #818cf8, #c4b5fd);
	-webkit-background-clip: text;
	-moz-background-clip: text;
	background-clip: text;
	-webkit-text-fill-color: transparent;
}

.price {
	font-size: 3.5rem;
	font-weight: 700;
	margin: 2rem 0;
	position: relative;
	display: inline-block;
}

.price::before {
	content: attr(data-annual);
	position: absolute;
	top: -1.5rem;
	left: 0;
	font-size: 1rem;
	color: #94a3b8;
	text-decoration: line-through;
	opacity: 0;
	transition: opacity 0.3s ease;
}

.price.active::before {
	opacity: 1;
}

.price span {
	font-size: 1.5rem;
	color: #94a3b8;
	margin-left: 0.5rem;
}

.features-list {
	list-style: none;
	margin-bottom: 3rem;
}

.features-list li {
	display: flex;
	align-items: center;
	margin-bottom: 1rem;
	padding: 0.8rem 0;
	border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}

.features-list li:last-child {
	border-bottom: none;
}

.features-list li svg {
	width: 20px;
	height: 20px;
	margin-right: 1rem;
	fill: #818cf8;
}

.cta-button {
	width: 100%;
	padding: 1.2rem;
	border: none;
	border-radius: 10px;
	background: linear-gradient(45deg, #818cf8, #6366f1);
	color: white;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 0.5rem;
	position: relative;
	overflow: hidden;
}

.cta-button::after {
	content: 'â†’';
	opacity: 0;
	transition: all 0.3s ease;
}

.cta-button:hover {
	transform: translateY(-2px);
	box-shadow: 0 5px 15px rgba(129, 140, 248, 0.3);
}

.cta-button:hover::after {
	opacity: 1;
	transform: translateX(5px);
}

.billing-switch {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
	margin-bottom: 3rem;
	background: rgba(255, 255, 255, 0.05);
	padding: 0.8rem 1.5rem;
	border-radius: 50px;
	position: relative;
}

.switch-label {
	cursor: pointer;
	padding: 0.8rem 2rem;
	border-radius: 30px;
	transition: all 0.3s ease;
	z-index: 1;
}

.switch-bg {
	position: absolute;
	background: linear-gradient(45deg, #818cf8, #6366f1);
	border-radius: 30px;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes rotate {
	100% {
		transform: rotate(360deg);
	}
}

.recommended-badge {
	position: absolute;
	top: -15px;
	right: -15px;
	background: #4f46e5;
	color: white;
	padding: 0.5rem 1.5rem;
	border-radius: 30px;
	font-size: 0.9rem;
	font-weight: 600;
	transform: rotate(15deg);
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}

.enterprise-card {
	border: 2px solid #4f46e5;
}

.enterprise-card .cta-button {
	background: linear-gradient(45deg, #4f46e5, #3730a3);
}

script:
function switchPricing(cycle) {
	const prices = document.querySelectorAll('.price');
	const switchLabels = document.querySelectorAll('.switch-label');
	const switchBg = document.querySelector('.switch-bg');

	// Update active state
	switchLabels.forEach((label) => {
		label.classList.remove('active');
		if (label.textContent.toLowerCase() === cycle) {
			label.classList.add('active');
			switchBg.style.width = `${label.offsetWidth}px`;
			switchBg.style.left = `${label.offsetLeft}px`;
		}
	});

	// Update prices
	prices.forEach((price) => {
		const newPrice =
			cycle === 'annual' ? price.getAttribute('data-annual') : price.getAttribute('data-monthly');

		price.querySelector('.currency').nextSibling.textContent = newPrice;
		price.classList.toggle('active', cycle === 'annual');
	});
}

// Initialize switch position
document.addEventListener('DOMContentLoaded', () => {
	const activeLabel = document.querySelector('.switch-label.active');
	const switchBg = document.querySelector('.switch-bg');
	switchBg.style.width = `${activeLabel.offsetWidth}px`;
	switchBg.style.left = `${activeLabel.offsetLeft}px`;
});
