style:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

:root {
	--primary: #6366f1;
	--secondary: #4f46e5;
	--accent: #818cf8;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', sans-serif;
}

body {
	background: #f8fafc;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 4rem 2rem;
}

.pricing-container {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2.5rem;
	max-width: 1200px;
	width: 100%;
}

.pricing-card {
	background: white;
	border-radius: 20px;
	padding: 2rem;
	box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
	position: relative;
	overflow: hidden;

 display: flex;
flex-direction: column;
     justify-content: space-between;
 }

.pricing-card:hover {
	transform: translateY(-5px);
}

.plan-header {
	text-align: center;
	margin-bottom: 2rem;
	position: relative;
}

.plan-name {
	font-size: 1.8rem;
	font-weight: 700;
	color: var(--primary);
	margin-bottom: 1rem;
}

.price-container {
	perspective: 1000px;
	margin: 2rem 0;
}

.price-wrapper {
	position: relative;
	transition: transform 0.6s;
	transform-style: preserve-3d;
}

.price-front,
.price-back {
	backface-visibility: hidden;
	position: absolute;
	width: 100%;
	height: 100%;
}

.price-back {
	transform: rotateX(180deg);
	color: var(--secondary);
}

.price {
	font-size: 3.5rem;
	font-weight: 800;
	color: var(--primary);
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 0.25rem;
}

.price span {
	font-size: 1.5rem;
	color: #64748b;
}

.features-list {
	list-style: none;
	margin: 2rem 0;
}

.features-list li {
	display: flex;
	align-items: center;
	padding: 1rem 0;
	border-bottom: 1px solid #e2e8f0;
	color: #475569;
}

.features-list li:last-child {
	border-bottom: none;
}

.features-list li svg {
	width: 24px;
	height: 24px;
	margin-right: 1rem;
	fill: var(--primary);
}

.cta-button {
	width: 100%;
	padding: 1.25rem;
	border: none;
	border-radius: 12px;
	background: var(--primary);
	color: white;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.cta-button:hover {
	background: var(--secondary);
	transform: rotateX(15deg);
	box-shadow: 0 5px 15px rgba(99, 102, 241, 0.3);
}

.billing-toggle {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
	margin-bottom: 3rem;
	background: white;
	padding: 0.75rem;
	border-radius: 50px;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.toggle-label {
	padding: 0.75rem 2rem;
	border-radius: 30px;
	cursor: pointer;
	transition: all 0.3s ease;
}

.toggle-label.active {
	background: var(--primary);
	color: white;
	box-shadow: 0 5px 15px rgba(99, 102, 241, 0.3);
}

.recommended-badge {
	position: absolute;
	top: -15px;
	right: -15px;
	background: var(--secondary);
	color: white;
	padding: 0.5rem 1.5rem;
	border-radius: 30px;
	transform: rotate(15deg);
	font-size: 0.9rem;
	font-weight: 600;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

@keyframes flip {
	0% {
		transform: rotateX(0);
	}
	50% {
		transform: rotateX(-90deg);
	}
	100% {
		transform: rotateX(0);
	}
}

.price-flip {
	animation: flip 0.6s ease;
}

script:
function toggleBilling(cycle) {
	const labels = document.querySelectorAll('.toggle-label');
	const priceWrappers = document.querySelectorAll('.price-wrapper');

	labels.forEach((label) => {
		label.classList.remove('active');
		if (label.textContent.toLowerCase() === cycle) {
			label.classList.add('active');
		}
	});

	priceWrappers.forEach((wrapper) => {
		wrapper.classList.add('price-flip');
		setTimeout(() => {
			wrapper.style.transform = cycle === 'annual' ? 'rotateX(180deg)' : 'rotateX(0deg)';
			wrapper.classList.remove('price-flip');
		}, 300);
	});
}

// Add button hover effect
document.querySelectorAll('.cta-button').forEach((button) => {
	button.addEventListener('mouseenter', () => {
		button.style.transform = 'rotateX(15deg)';
	});

	button.addEventListener('mouseleave', () => {
		button.style.transform = 'rotateX(0deg)';
	});
});
