style:
:root {
	--background-color: #fafafa;
	--text-color: #333;
	--card-background: white;
	--card-border: #eee;
	--price-color: #222;
	--feature-color: #666;
	--feature-border: #f0f0f0;
	--cta-background: #222;
	--cta-color: white;
	--cta-hover-background: transparent;
	--cta-hover-color: #222;
}

[data-theme="dark"] {
	--background-color: #121212;
	--text-color: #e0e0e0;
	--card-background: #1e1e1e;
	--card-border: #333;
	--price-color: #e0e0e0;
	--feature-color: #b0b0b0;
	--feature-border: #333;
	--cta-background: #333;
	--cta-color: #e0e0e0;
	--cta-hover-background: transparent;
	--cta-hover-color: #e0e0e0;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: var(--background-color);
	color: var(--text-color);
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	padding: 2rem;
	transition: background 0.3s ease, color 0.3s ease;
}

.pricing-container {
	max-width: 1200px;
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
	gap: 2rem;
	width: 100%;
}

.pricing-card {
	background: var(--card-background);
	border: 1px solid var(--card-border);
	border-radius: 8px;
	padding: 2.5rem 2rem;
	text-align: center;
	transition: all 0.2s ease;
}

.pricing-card:hover {
	border-color: var(--feature-border);
}

.plan-name {
	color: var(--text-color);
	font-size: 1.4rem;
	font-weight: 500;
	margin-bottom: 1rem;
}

.price {
	font-size: 2.5rem;
	color: var(--price-color);
	margin: 1.5rem 0;
	font-weight: 300;
}

.price span {
	font-size: 1rem;
	color: var(--feature-color);
}

.features {
	list-style: none;
	margin: 2rem 0;
}

.features li {
	color: var(--feature-color);
	padding: 0.8rem 0;
	border-bottom: 1px solid var(--feature-border);
}

.features li:last-child {
	border-bottom: none;
}

.cta {
	display: inline-block;
	width: 100%;
	padding: 1rem;
	background: var(--cta-background);
	color: var(--cta-color);
	text-decoration: none;
	border-radius: 4px;
	font-weight: 500;
	transition: all 0.2s ease;
	border: 1px solid var(--cta-background);
}

.cta:hover {
	background: var(--cta-hover-background);
	color: var(--cta-hover-color);
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: var(--cta-background);
	color: var(--cta-color);
	border: none;
	padding: 0.5rem 1rem;
	border-radius: 4px;
	cursor: pointer;
	font-weight: 500;
}

@media (max-width: 768px) {
	.pricing-container {
		grid-template-columns: 1fr;
	}
}

script:
function toggleTheme() {
	const body = document.body;
	const currentTheme = body.getAttribute("data-theme");
	if (currentTheme === "dark") {
		body.removeAttribute("data-theme");
	} else {
		body.setAttribute("data-theme", "dark");
	}
}
