style:
.product-card {
	max-width: 300px;
	border-radius: 10px;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	font-family: Arial, sans-serif;
	margin: 20px;
	background: white;
	transition: transform 0.3s ease;
}

.product-card:hover {
	transform: translateY(-5px);
}

.product-image {
	width: 100%;
	height: 200px;
	object-fit: cover;
}

.product-info {
	padding: 20px;
}

.product-title {
	font-size: 1.2em;
	margin: 0 0 10px 0;
	color: #333;
}

.product-description {
	font-size: 0.9em;
	color: #666;
	margin: 0 0 15px 0;
	line-height: 1.4;
}

.product-price {
	font-size: 1.4em;
	color: #2ecc71;
	margin: 0 0 15px 0;
	font-weight: bold;
}

.old-price {
	text-decoration: line-through;
	color: #999;
	font-size: 0.9em;
	margin-left: 10px;
}

.buy-button {
	background-color: #3498db;
	color: white;
	border: none;
	padding: 10px 20px;
	border-radius: 5px;
	cursor: pointer;
	width: 100%;
	font-size: 1em;
	transition: background-color 0.3s ease;
}

.buy-button:hover {
	background-color: #2980b9;
}

.rating {
	color: #f1c40f;
	margin-bottom: 10px;
}

.reviews {
	font-size: 0.8em;
	color: #666;
	margin-left: 5px;
}
