style:
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	font-family: "Arial", sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: #f8f9fa;
}

.card {
	max-width: 300px;
	background: white;
	border-radius: 16px;
	overflow: hidden;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	transition: 0.3s ease;
}

.card:hover {
	transform: translateY(-5px);
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
}

.card img {
	width: 100%;
	height: auto;
}

.card-content {
	padding: 16px;
}

.card-title {
	font-size: 1.4rem;
	font-weight: bold;
	margin-bottom: 8px;
}

.card-description {
	font-size: 0.9rem;
	color: #555;
	margin-bottom: 16px;
}

.price {
	font-size: 1.2rem;
	color: #333;
	font-weight: bold;
	margin-bottom: 12px;
}

.btn {
	display: inline-block;
	text-align: center;
	background-color: #007bff;
	color: white;
	padding: 10px 20px;
	border: none;
	border-radius: 8px;
	text-decoration: none;
	transition: background-color 0.2s ease;
}

.btn:hover {
	background-color: #0056b3;
}
