style:
@font-face {
	font-family: Kode Mono;
	font-style: normal;
	font-weight: 400 700;
	font-display: swap;
	src: url(https://fonts.gstatic.com/s/kodemono/v2/A2BYn5pb0QgtVEPFnlYOnYLw.woff2) format("woff2");
	unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+0304, U+0308, U+0329, U+2000-206F, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	font-family: Kode Mono, monospace;
	font-optical-sizing: auto;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	color: white;
}

.card {
	max-width: 320px;
	background: linear-gradient(135deg, #1f1c47, #6c63ff);
	border-radius: 20px;
	overflow: hidden;
	backdrop-filter: blur(15px);
	box-shadow: 0 8px 24px rgba(0, 0, 0, 0.3);
	transition: transform 0.4s, box-shadow 0.4s;
}

.card:hover {
	transform: translateY(-10px) scale(1.03);
	box-shadow: 0 12px 30px rgba(0, 0, 0, 0.5);
}

.card img {
	width: 100%;
	height: 200px;
	object-fit: cover;
}

.card-content {
	padding: 20px;
	text-align: center;
}

.card-title {
	font-size: 1.6rem;
	font-weight: bold;
	margin-bottom: 8px;
	color: #fff;
}

.card-description {
	font-size: 0.95rem;
	color: #ddd;
	margin-bottom: 16px;
}

.price {
	font-size: 1.4rem;
	color: #ffeb3b;
	font-weight: bold;
	margin-bottom: 12px;
}

.btn {
	display: inline-block;
	text-align: center;
	background: linear-gradient(135deg, #6c63ff, #3f51b5);
	color: white;
	padding: 12px 24px;
	border: none;
	border-radius: 12px;
	text-decoration: none;
	font-weight: bold;
	transition: all 0.3s ease;
}

.btn:hover {
	background: linear-gradient(135deg, #3f51b5, #6c63ff);
}
