style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
	background-color: #f0f0f0;
	font-family: "Arial", sans-serif;
}

.comic-card {
	position: relative;
	width: 300px;
	background: #fff;
	border-radius: 15px;
	padding: 20px;
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
	border: 4px solid #000;
	transform: rotate(-2deg);
	transition: transform 0.3s ease;
}

.comic-card:hover {
	transform: rotate(0deg) scale(1.05);
}

.comic-image {
	width: 100%;
	height: 200px;
	border: 3px solid #000;
	border-radius: 10px;
	margin-bottom: 15px;
	object-fit: cover;
}

.comic-title {
	font-size: 1.5em;
	font-weight: bold;
	margin: 0 0 10px 0;
	color: #e74c3c;
	text-transform: uppercase;
	text-shadow: 2px 2px 0 #000;
}

.comic-description {
	font-size: 0.9em;
	margin-bottom: 15px;
	line-height: 1.4;
	font-style: italic;
}

.comic-price {
	display: inline-block;
	background: #2ecc71;
	color: white;
	padding: 5px 15px;
	border-radius: 5px;
	font-weight: bold;
	border: 2px solid #000;
	box-shadow: 3px 3px 0 #000;
}

.comic-button {
	display: block;
	width: 100%;
	padding: 10px;
	background: #f1c40f;
	border: 3px solid #000;
	border-radius: 8px;
	font-weight: bold;
	text-transform: uppercase;
	cursor: pointer;
	margin-top: 15px;
	transition: all 0.2s ease;
}

.comic-button:hover {
	background: #f39c12;
	transform: translateY(-2px);
	box-shadow: 4px 4px 0 #000;
}

.comic-badge {
	position: absolute;
	top: -15px;
	right: -15px;
	background: #e74c3c;
	color: white;
	padding: 8px 12px;
	border-radius: 50%;
	border: 3px solid #000;
	font-weight: bold;
	transform: rotate(5deg);
}

.comic-badge::after {
	content: "";
	position: absolute;
	top: 5px;
	right: 5px;
	width: 10px;
	height: 10px;
	background: rgba(255, 255, 255, 0.5);
	border-radius: 50%;
}
