style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
	background: linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 100%);
	font-family: "Impact", sans-serif;
}

.comic-container {
	background: white;
	width: 320px;
	padding: 25px;
	border: 5px solid #000;
	border-radius: 25px;
	box-shadow: 10px 10px 0 rgba(0, 0, 0, 0.2);
	position: relative;
	transition: all 0.3s ease;
}

.comic-container:hover {
	transform: translateY(-5px);
	box-shadow: 15px 15px 0 rgba(0, 0, 0, 0.2);
}

.hero-image {
	width: 100%;
	height: 240px;
	border: 4px solid #000;
	border-radius: 15px;
	margin-bottom: 20px;
	object-fit: cover;
	background: #ffd93d;
	position: relative;
}

.speech-bubble {
	position: absolute;
	top: -15px;
	left: -15px;
	background: #ff9f1c;
	color: white;
	padding: 12px 18px;
	border: 3px solid #000;
	border-radius: 15px;
	font-size: 1.2em;
	clip-path: polygon(0% 0%, 100% 0%, 100% 75%, 75% 75%, 75% 100%, 50% 75%, 0% 75%);
}

.product-title {
	color: #2f4858;
	margin: 0 0 15px 0;
	font-size: 2em;
	text-shadow: 3px 3px 0 #ffd93d;
	letter-spacing: -2px;
}

.power-stats {
	display: flex;
	justify-content: space-between;
	margin-bottom: 20px;
}

.stat-item {
	text-align: center;
	padding: 10px;
	background: #f8f9fa;
	border: 2px solid #000;
	border-radius: 10px;
	flex: 1;
	margin: 0 5px;
}

.stat-label {
	font-size: 0.8em;
	color: #6c757d;
}

.stat-value {
	font-size: 1.4em;
	font-weight: bold;
	color: #2b2b2b;
}

.comic-button {
	display: block;
	width: 100%;
	padding: 15px;
	background: #4ecdc4;
	border: 4px solid #000;
	border-radius: 12px;
	font-size: 1.2em;
	font-weight: bold;
	text-transform: uppercase;
	cursor: pointer;
	transition: all 0.2s ease;
	position: relative;
	overflow: hidden;
}

.comic-button:hover {
	background: #45b7af;
	transform: skew(-5deg);
	box-shadow: 5px 5px 0 #000;
}

.comic-button::before {
	content: "⚡";
	position: absolute;
	right: 10px;
	top: 50%;
	transform: translateY(-50%);
	font-size: 1.5em;
	animation: zap 0.8s infinite;
}

@keyframes zap {
	0% {
		opacity: 0;
		transform: translateY(-50%) translateX(20px);
	}
	50% {
		opacity: 1;
	}
	100% {
		opacity: 0;
		transform: translateY(-50%) translateX(-20px);
	}
}

.motion-lines {
	position: absolute;
	bottom: -30px;
	right: -20px;
	font-size: 2em;
	color: #000;
	opacity: 0.3;
	transform: rotate(15deg);
}

.limited-offer {
	position: absolute;
	bottom: -15px;
	left: 50%;
	transform: translateX(-50%);
	background: #ff4757;
	color: white;
	padding: 8px 20px;
	border: 3px solid #000;
	border-radius: 25px;
	font-size: 0.9em;
}

.price-tag {
	position: absolute;
	top: 200px;
	right: -15px;
	background: #2ed573;
	padding: 10px 25px;
	border: 3px solid #000;
	border-radius: 15px;
	font-size: 1.4em;
	transform: rotate(5deg);
	box-shadow: 5px 5px 0 rgba(0, 0, 0, 0.2);
}

.original-price {
	text-decoration: line-through;
	color: #747d8c;
	margin-left: 10px;
	font-size: 0.8em;
}
