style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: monospace;
}

html,
body {
	height: 100%;
	width: 100%;
}

body {
	display: flex;
	align-items: center;
	justify-content: center;
	background-color: #28282b;
}

#card {
	border-radius: 10px;
	height: 450px;
	width: 300px;
	background-color: white;
	padding: 15px;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
}

#card img {
	height: 70%;
	width: 100%;
	object-fit: cover;
	object-position: center;
	border-radius: 10px;
	position: relative;
}

#card h3 {
	font-size: 22px;
	margin-top: 8px;
}

#card p {
	font-size: 14px;
	margin-bottom: 20px;
}

#card h5 {
	font-size: 18px;
	color: #1cac78;
	margin-bottom: 10px;
}

#ctn {
	background-color: #28282b;
	color: white;
	width: 50%;
	font-weight: 700;
	font-size: 15px;
	padding: 8px 12px;
	margin: 5px;
	border: none;
	border-radius: 10px;
	cursor: pointer;
	transform: all 0.3s ease;
}

#ctn:hover {
	background-color: #333336;
}
