style:
:root {
	--primary: #ff6b6b;
	--secondary: #ff8e8e;
	--background: #2d3436; /* Dark background by default */
	--text: #f4f4f4; /* Light text by default */
	--card-bg: #3d4548; /* Dark card background */
	--shadow-color: rgba(0, 0, 0, 0.2);
}

body {
	font-family: "Poppins", sans-serif;
	background: var(--background);
	color: var(--text);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	margin: 0;
	transition: all 0.5s ease;
}

.card-container {
	perspective: 1000px;
	width: 300px;
	height: 400px;
}

.profile-card {
	width: 100%;
	height: 100%;
	transform-style: preserve-3d;
	transition: all 0.8s ease;
	position: relative;
	cursor: pointer;
}

.profile-card:hover {
	transform: rotateY(180deg);
}

.card-face {
	position: absolute;
	width: 100%;
	height: 100%;
	backface-visibility: hidden;
	border-radius: 20px;
	box-shadow: 0 10px 30px var(--shadow-color);
	overflow: hidden;
	background: var(--card-bg);
}

.front {
	display: flex;
	flex-direction: column;
	align-items: center;
	padding: 2rem;
}

.back {
	transform: rotateY(180deg);
	padding: 2rem;
	display: flex;
	flex-direction: column;
	justify-content: center;
}

.profile-image {
	width: 120px;
	height: 120px;
	border-radius: 50%;
	margin-bottom: 1.5rem;
	position: relative;
	overflow: hidden;
	transition: transform 0.3s ease;
}

.profile-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.name {
	color: var(--text);
	margin: 0 0 0.5rem;
	font-size: 1.5rem;
}

.title {
	color: var(--primary);
	font-weight: 500;
	margin-bottom: 1rem;
}

.bio {
	color: var(--text);
	text-align: center;
	font-size: 0.9rem;
	line-height: 1.6;
	opacity: 0.8;
}

.stats {
	display: flex;
	gap: 1.2rem;
	justify-content: space-between;
	margin: 1.5rem 0;
}

.stat {
	text-align: center;
}

.stat-number {
	color: var(--primary);
	font-weight: 700;
	font-size: 1.2rem;
}

.stat-label {
	color: var(--text);
	font-size: 0.8rem;
	opacity: 0.7;
}

.social-links {
	display: flex;
	justify-content: center;
	gap: 1.5rem;
}

.social-links a {
	color: var(--text);
	font-size: 1.4rem;
	transition: color 0.3s ease;
}

.social-links a:hover {
	color: var(--primary);
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 50%;
	width: 40px;
	height: 40px;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: all 0.3s ease;
	z-index: 1000;
}

.theme-toggle:hover {
	transform: scale(1.1);
}

@media (max-width: 480px) {
	.card-container {
		width: 90%;
		height: 450px;
	}
}

script:
let isDark = true; // Default dark mode

function toggleTheme() {
	isDark = !isDark;
	const root = document.documentElement;
	const toggleBtn = document.querySelector(".theme-toggle");

	if (isDark) {
		root.style.setProperty("--background", "#2d3436");
		root.style.setProperty("--text", "#f4f4f4");
		root.style.setProperty("--card-bg", "#3d4548");
		root.style.setProperty("--shadow-color", "rgba(0, 0, 0, 0.2)");
		toggleBtn.innerHTML = '<i class="fas fa-sun"></i>';
	} else {
		root.style.setProperty("--background", "#f4f4f4");
		root.style.setProperty("--text", "#2d3436");
		root.style.setProperty("--card-bg", "#ffffff");
		root.style.setProperty("--shadow-color", "rgba(0, 0, 0, 0.1)");
		toggleBtn.innerHTML = '<i class="fas fa-moon"></i>';
	}
}

// Mobile flip interaction
const card = document.querySelector(".profile-card");
card.addEventListener("click", function () {
	if (window.innerWidth <= 768) {
		this.classList.toggle("flipped");
	}
});
