style:
:root {
	--primary: #7c3aed;
	--glass: rgba(255, 255, 255, 0.1);
	--text: #ffffff;
}

body {
	font-family: "Inter", sans-serif;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	margin: 0;
	background: url("https://picsum.photos/id/795/1920/1080") center/cover fixed;
	position: relative;
}

body::after {
	content: "";
	position: absolute;
	inset: 0;
	background: rgba(0, 0, 0, 0.5);
	z-index: -1;
}

.profile-card {
	background: var(--glass);
	backdrop-filter: blur(10px);
	border-radius: 20px;
	padding: 2rem;
	width: 320px;
	box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
	border: 1px solid rgba(255, 255, 255, 0.1);
	position: relative;
	overflow: hidden;
	transition: transform 0.3s ease;
}

.profile-content {
	position: relative;
	z-index: 1;
	text-align: center;
}

.profile-image {
	width: 120px;
	height: 120px;
	border-radius: 50%;
	margin: 0 auto 1.5rem;
	overflow: hidden;
	border: 3px solid var(--text);
	transition: transform 0.3s ease;
	position: relative;
}

.profile-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.name {
	color: var(--text);
	margin: 0 0 0.5rem;
	font-size: 1.8rem;
}

.role {
	color: var(--primary);
	font-weight: 500;
	margin-bottom: 1.5rem;
}

.stats {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1rem;
	margin: 2rem 0;
}

.stat-item {
	background: var(--glass);
	padding: 1rem;
	border-radius: 10px;
	backdrop-filter: blur(5px);
}

.stat-number {
	color: var(--text);
	font-weight: 700;
	font-size: 1.2rem;
}

.stat-label {
	color: var(--primary);
	font-size: 0.8rem;
}

.social-links {
	display: flex;
	justify-content: center;
	gap: 1.5rem;
}

.social-links a {
	width: 40px;
	height: 40px;
	border-radius: 50%;
	background: var(--glass);
	display: flex;
	align-items: center;
	justify-content: center;
	color: var(--text);
	text-decoration: none;
	transition: all 0.3s ease;
}

.social-links a:hover {
	background: var(--primary);
	transform: translateY(-3px);
}

.parallax-bg {
	position: absolute;
	width: 150%;
	height: 150%;
	background: url("https://picsum.photos/id/795/1920/1080") center/cover;
	top: -25%;
	left: -25%;
	z-index: 0;
	pointer-events: none;
}

@media (max-width: 480px) {
	.profile-card {
		width: 90%;
	}
}

script:
document.addEventListener("mousemove", (e) => {
	const parallaxBg = document.querySelector(".parallax-bg");
	const xAxis = (window.innerWidth / 2 - e.pageX) / 30;
	const yAxis = (window.innerHeight / 2 - e.pageY) / 30;
	parallaxBg.style.transform = `translate(${-xAxis}px, ${-yAxis}px)`;
});

document.querySelector(".profile-card").addEventListener("mouseleave", () => {
	const parallaxBg = document.querySelector(".parallax-bg");
	parallaxBg.style.transform = "translate(0, 0)";
});
