style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	background: #f4f4f4;
	font-family: Arial, sans-serif;
}

.profile-card {
	background: white;
	width: 300px;
	padding: 20px;
	text-align: center;
	border-radius: 15px;
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease-in-out;
}

.profile-card:hover {
	transform: translateY(-5px);
}

.profile-card img {
	width: 100px;
	height: 100px;
	border-radius: 50%;
	object-fit: cover;
	border: 4px solid #007bff;
}

.profile-card h2 {
	margin: 15px 0 5px;
	font-size: 22px;
}

.profile-card p {
	color: #666;
	font-size: 14px;
}

.social-icons {
	margin-top: 15px;
}

.social-icons a {
	margin: 0 8px;
	color: #007bff;
	font-size: 20px;
	transition: color 0.3s ease-in-out;
}

.social-icons a:hover {
	color: #0056b3;
}
