style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	background: linear-gradient(to right, #6a11cb, #2575fc);
	font-family: "Poppins", sans-serif;
}

.profile-card {
	background: white;
	width: 350px;
	padding: 25px;
	text-align: center;
	border-radius: 20px;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
	transition: transform 0.3s ease-in-out;
}

.profile-card:hover {
	transform: translateY(-5px);
}

.profile-card img {
	width: 120px;
	height: 120px;
	border-radius: 50%;
	object-fit: cover;
	border: 5px solid #2575fc;
}

.profile-card h2 {
	margin: 15px 0 5px;
	font-size: 24px;
	color: #333;
}

.profile-card p {
	color: #777;
	font-size: 16px;
	margin-bottom: 15px;
}

.social-icons {
	margin: 10px 0;
}

.social-icons a {
	margin: 0 10px;
	color: #2575fc;
	font-size: 22px;
	transition: color 0.3s ease-in-out;
}

.social-icons a:hover {
	color: #6a11cb;
}

.btn-container {
	margin-top: 15px;
}

.btn {
	text-decoration: none;
	padding: 10px 20px;
	margin: 5px;
	border-radius: 25px;
	border: none;
	cursor: pointer;
	font-size: 16px;
	transition: background 0.3s ease-in-out;
}

.btn-follow {
	background: #2575fc;
	color: white;
}

.btn-follow:hover {
	background: #6a11cb;
}

.btn-message {
	background: #f4f4f4;
	color: #333;
}

.btn-message:hover {
	background: #ddd;
}
