style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	background: #f0f2f5;
	font-family: "Poppins", sans-serif;
}

.profile-card {
	background: white;
	max-width: 330px;
	border-radius: 30px;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
	text-align: center;
	padding: 30px;
	position: relative;
}

.profile-photo {
	width: 110px;
	height: 110px;
	border-radius: 50%;
	object-fit: cover;
	border: 5px solid white;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	margin-top: -25%;
	background: white;
}

.profile-card h2 {
	margin: 10px 0 5px;
	font-size: 24px;
	color: #333;
}

.profile-card p {
	color: #777;
	font-size: 15px;
	margin-bottom: 15px;
}

.social-icons {
	margin: 15px 0;
}

.social-icons a {
	margin: 0 10px;
	color: #007bff;
	font-size: 22px;
	transition: color 0.3s ease-in-out;
}

.social-icons a:hover {
	color: #0056b3;
}

.btn-container {
	display: flex;
	justify-content: center;
	gap: 15px;
	margin-top: 10px;
}

.btn {
	text-decoration: none;
	padding: 10px 20px;
	border-radius: 30px;
	border: none;
	cursor: pointer;
	font-size: 16px;
	transition: background 0.3s ease-in-out;
}

.btn-follow {
	background: #007bff;
	color: white;
}

.btn-follow:hover {
	background: #0056b3;
}

.btn-message {
	background: #f4f4f4;
	color: #333;
}

.btn-message:hover {
	background: #ddd;
}
