style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	background: #1e1e1e;
	font-family: "Poppins", sans-serif;
}

.profile-card {
	background: #2a2a2a;
	width: 360px;
	border-radius: 20px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
	text-align: center;
	padding: 30px;
	position: relative;
	color: #ffffff;
}

.profile-photo {
	width: 120px;
	height: 120px;
	border-radius: 50%;
	object-fit: cover;
	border: 5px solid #007bff;
	box-shadow: 0 5px 15px rgba(0, 123, 255, 0.5);
	margin-top: -60px;
	background: white;
}

.profile-card h2 {
	margin: 15px 0 5px;
	font-size: 26px;
	color: #fff;
}

.profile-card p {
	color: #bbb;
	font-size: 14px;
	margin-bottom: 15px;
}

.social-icons {
	margin: 15px 0;
}

.social-icons a {
	margin: 0 12px;
	color: #007bff;
	font-size: 24px;
	transition: transform 0.3s ease-in-out, color 0.3s ease-in-out;
}

.social-icons a:hover {
	color: #00d4ff;
	transform: scale(1.2);
}

.btn-container {
	display: flex;
	justify-content: center;
	gap: 15px;
	margin-top: 15px;
}

.btn {
	text-decoration: none;
	padding: 12px 22px;
	border-radius: 25px;
	border: none;
	cursor: pointer;
	font-size: 16px;
	transition: background 0.3s ease-in-out, transform 0.2s;
}

.btn-follow {
	background: #007bff;
	color: white;
}

.btn-follow:hover {
	background: #0056b3;
	transform: scale(1.05);
}

.btn-message {
	background: #333;
	color: white;
	border: 1px solid #555;
}

.btn-message:hover {
	background: #444;
	transform: scale(1.05);
}
