style:
:root {
  --bg-color: #1a1a1a;
  --card-bg: #202020;
  --accent: #8b5cf6;
  --text: #ffffff;
  --secondary-text: #888;
  --radius: 20px;
  --spacing: 16px;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background: var(--bg-color);
  font-family: "Inter", system-ui, sans-serif;
  min-height: 100vh;
  display: grid;
  place-items: center;
  line-height: 1.5;
}

.dark-card {
  width: min(340px, 90%);
  background: var(--card-bg);
  border-radius: var(--radius);
  padding: var(--spacing);
  box-shadow: 20px 20px 60px #151515, -20px -20px 60px #252525;
}

.banner {
  height: 100px;
  margin: calc(-1 * var(--spacing)) calc(-1 * var(--spacing)) 0;
  border-radius: var(--radius) var(--radius) 0 0;
  background: linear-gradient(45deg, var(--accent), #4017a1);
  position: relative;
  overflow: hidden;
}

.banner::after {
  content: "";
  position: absolute;
  inset: 0;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.4));
}

.profile-section {
  margin-top: -40px;
  position: relative;
  z-index: 1;
  text-align: center;
}

.profile-img {
  width: 90px;
  height: 90px;
  border-radius: 18px;
  border: 4px solid var(--card-bg);
  margin-bottom: 1rem;
  object-fit: cover;
}

.user-name {
  color: var(--text);
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.user-handle {
  color: var(--secondary-text);
  font-size: 0.875rem;
  margin-bottom: 1rem;
}

.badges {
  display: flex;
  justify-content: center;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
}

.badge {
  padding: 0.25rem 0.75rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  font-size: 0.75rem;
  color: var(--text);
}

.badge.premium {
  display: flex;
  align-items: center;
  padding: 0 20px;
  background: linear-gradient(45deg, #ffd700, #ffa500);
}

.stats-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1rem;
  margin-bottom: 1.5rem;
  padding: 1rem;
  background: #252525;
  border-radius: 16px;
}

.stat-item {
  text-align: center;
}

.stat-value {
  color: var(--text);
  font-size: 1.125rem;
  font-weight: 600;
}

.stat-label {
  color: var(--secondary-text);
  font-size: 0.75rem;
}

.action-buttons {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.75rem;
  margin-top: 1.5rem;
}

.btn {
  padding: 0.75rem;
  border-radius: 12px;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  border: none;
}

.btn-primary {
  background: var(--accent);
  color: var(--text);
}

.btn-primary:hover {
  background: #4017a1;
  transform: translateY(-2px);
}

.btn-secondary {
  background: #2a2a2a;
  color: var(--text);
  border: 1px solid #333;
}

.btn-secondary:hover {
  background: #303030;
  transform: translateY(-2px);
}

.skills-section {
  margin: 1.5rem 0;
}

.skills-title {
  color: var(--secondary-text);
  font-size: 0.75rem;
  margin-bottom: 1rem;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.skills-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 0.75rem;
}

.skill-card {
  background: #252525;
  padding: 0.75rem;
  border-radius: 12px;
  text-align: center;
  transition: transform 0.2s ease;
}

.skill-card:hover {
  transform: translateY(-2px);
}

.skill-name {
  color: var(--text);
  font-size: 0.875rem;
  font-weight: 500;
}

.skill-level {
  color: var(--secondary-text);
  font-size: 0.75rem;
  margin-top: 0.25rem;
}

script:
const followBtn = document.getElementById("followBtn");
let isFollowing = false;

followBtn.addEventListener("click", () => {
  isFollowing = !isFollowing;
  followBtn.textContent = isFollowing ? "Following" : "Follow";
  followBtn.style.background = isFollowing ? "#2a2a2a" : "#8b5cf6";
});
