style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	background: #171717;
	min-height: 100vh;
}

.card {
	min-width: 350px;
	width: 400px;
	aspect-ratio: 4/5;
	position: relative;
	cursor: pointer;
}

h3 {
	font-size: 1.4rem;
	color: #f6f6f6;
}

p {
	font-size: clamp(0.85rem, 0.75rem + 0.32vw, 1.15rem);
	letter-spacing: 0.12rem;
	font-weight: 300;
	max-width: 100%;
}

.card::before {
	position: absolute;
	content: "";
	width: 5rem;
	height: 6.25rem;
	z-index: 0;
	transition: 0.3s ease-in-out;
	background: #f2709c;
	background: -webkit-linear-gradient(to left, #ff9472, #f2709c);
	background: linear-gradient(to left, #ff9472, #f2709c);
	top: 0;
	left: 0;
}

.card::after {
	position: absolute;
	inset: 0;
	content: "";
	width: 100%;
	height: 100%;
	background: #232526;
	background: -webkit-linear-gradient(
		to bottom,
		hsl(210deg 2.99% 26.27% / 20%),
		hsl(0deg 0% 3.14% / 90%)
	);
	background: linear-gradient(
		to bottom,
		hsl(210deg 2.99% 26.27% / 20%),
		hsl(0deg 0% 3.14% / 90%)
	);
}

img {
	filter: grayscale(100%);
	transition: 0.5s ease;
	width: 100%;
	height: 100%;
	object-fit: cover;
	position: absolute;
}

.card-content {
	position: absolute;
	color: #fff;
	width: 100%;
	bottom: 0;
	padding: 1.875rem 1.25rem;
	text-align: center;
	z-index: 999;
	transition: all 0.3s ease;
}

ul {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 0.625rem;
	margin-top: 1.25rem;
	list-style-type: none;
}

ul li i {
	color: #f5f5f5;
	width: 25px;
}

.card:hover img {
	filter: grayscale(0%);
}

.card:hover::before {
	transform: scale(1.03);
}

.card:hover .card-content {
	bottom: 25px;
}
