style:
:root {
	--primary-color: #4a90e2;
	--secondary-color: #6a6a6a;
	--accent-color: #f5a623;
	--bg-color: #f8f9fa;
}

body {
	font-family: "Segoe UI", sans-serif;
	background-color: var(--bg-color);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	margin: 0;
}

.profile-card {
	background: white;
	border-radius: 15px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	width: 320px;
	padding: 2rem;
	text-align: center;
	position: relative;
	transition: transform 0.3s;
}

.profile-card:hover {
	transform: translateY(-5px);
}

.profile-img {
	width: 120px;
	height: 120px;
	border-radius: 50%;
	margin: -70px auto 20px;
	border: 4px solid white;
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	position: relative;
}

.profile-img img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.name {
	color: var(--primary-color);
	margin: 0 0 0.5rem;
	font-size: 1.8rem;
}

.title {
	color: var(--secondary-color);
	font-weight: 400;
	margin-bottom: 1.5rem;
}

.details {
	display: flex;
	justify-content: space-around;
	margin: 2rem 0;
}

.details-item {
	text-align: center;
}

.details-number {
	color: var(--primary-color);
	font-size: 1.4rem;
	font-weight: 600;
	display: block;
}

.details-label {
	color: var(--secondary-color);
	font-size: 0.9rem;
}

.social-links {
	display: flex;
	justify-content: center;
	gap: 1.5rem;
}

.social-links a {
	color: var(--secondary-color);
	font-size: 1.4rem;
	transition: color 0.3s;
}

.social-links a:hover {
	color: var(--accent-color);
}

@media (max-width: 480px) {
	.profile-card {
		width: 90%;
		padding: 1.5rem;
	}

	.name {
		font-size: 1.5rem;
	}
}
