style:
:root {
	--primary: #6c5ce7;
	--secondary: #a8a5e6;
	--text: #2d3436;
	--background: #f8f9fe;
}

body {
	font-family: "Inter", sans-serif;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	margin: 0;
}

.profile-card {
	background: white;
	border-radius: 20px;
	box-shadow: 0 15px 30px rgba(108, 92, 231, 0.1);
	width: 340px;
	padding: 30px;
	position: relative;
	overflow: hidden;
	transition: transform 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

.profile-img {
	width: 120px;
	height: 120px;
	border-radius: 50%;
	margin: 0 auto 20px;
	border: 3px solid white;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease;
	cursor: pointer;
}

.profile-img:hover {
	transform: scale(1.05);
}

.profile-img img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	border-radius: 50%;
}

.profile-info {
	text-align: center;
	margin-bottom: 25px;
}

.name {
	color: var(--text);
	margin: 0 0 5px;
	font-size: 1.8rem;
	font-weight: 700;
}

.position {
	color: var(--primary);
	font-weight: 500;
	font-size: 1rem;
	margin-bottom: 15px;
}

.description {
	color: var(--text);
	font-size: 0.9rem;
	line-height: 1.6;
	opacity: 0.8;
	margin-bottom: 25px;
}

.social-container {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 15px;
	margin-bottom: 20px;
}

.social-item {
	background: rgba(108, 92, 231, 0.08);
	padding: 12px;
	border-radius: 12px;
	text-align: center;
	transition: all 0.3s ease;
	cursor: pointer;
}

.social-item:hover {
	background: var(--primary);
	transform: translateY(-3px);
}

.social-item i {
	color: var(--primary);
	font-size: 1.4rem;
}

.social-item:hover i {
	color: white;
}

.action-button {
	display: inline-block;
	width: 100%;
	padding: 12px;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 10px;
	font-weight: 600;
	cursor: pointer;
	transition: all 0.3s ease;
}

.action-button:hover {
	transform: translateY(-2px);
	box-shadow: 0 5px 15px rgba(108, 92, 231, 0.3);
}

@media (max-width: 480px) {
	.profile-card {
		width: 90%;
		padding: 25px;
	}
}
