style:
body { 
  font-family: "Arial", sans-serif; 
  display: flex; 
  justify-content: center; 
  align-items: center; 
  min-height: 100vh; 
  background-color: #f5f5f5; 
  color: #333; 
} 

.container { 
  display: flex; 
  flex-wrap:wrap;  
  gap: 20px; 
} 

.card { 
  width: 280px; 
  padding: 20px; 
  border-radius: 12px; 
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); 
  transition: transform 0.3s; 
} 

.card:hover { 
  transform: translateY(-5px); 
} 

.icon { 
  font-size: 3rem; 
} 

.progress { 
  background-color: #f1f1f1; 
  border-radius: 5px; 
  overflow: hidden; 
  height: 10px; 
  margin-top: 10px; 
} 

.progress-bar { 
  height: 10px; 
  background-color: #007bff; 
} 

h2 { 
  margin-top: 10px; 
  font-size: 1.5rem; 
} 

p { 
  color: #666; 
  font-size: 0.9rem; 
} 

/* Dark Mode for First Card */ 

.card.dark-mode { 
  background-color: #1e1e1e; 
  color: #ffffff; 
} 

/* Light Mode for Second Card */ 

.card.light-mode { 
  background-color: #ffffff; 
  color: #333; 
}
