style:
body {
	font-family: "Arial", sans-serif;
	margin: 0;
	padding: 0;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: #f4f4f4;
	transition: background-color 0.3s, color 0.3s;
}

body.dark-mode {
	background-color: #333;
	color: #fff;
}

.container {
	display: flex;
	gap: 20px;
	flex-wrap: wrap;
	justify-content: center;
}

.card {
	background-color: #fff;
	border-radius: 15px;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	padding: 25px;
	width: 280px;
	text-align: center;
	transition: background-color 0.3s, color 0.3s;
}

body.dark-mode .card {
	background-color: #444;
	color: #fff;
}

.card h2 {
	margin-top: 0;
	font-size: 1.5rem;
}

.progress-circle {
	width: 100px;
	height: 100px;
	border-radius: 50%;
	background: conic-gradient(#007bff 75%, #e0e0e0 0);
	margin: 20px auto;
	display: flex;
	justify-content: center;
	align-items: center;
	position: relative;
}

.progress-circle::before {
	content: attr(data-progress);
	position: absolute;
	font-size: 1.2rem;
	font-weight: bold;
	color: #007bff;
}

body.dark-mode .progress-circle {
	background: conic-gradient(#1e90ff 75%, #555 0);
}

body.dark-mode .progress-circle::before {
	color: #1e90ff;
}

.progress-bar {
	width: 100%;
	height: 10px;
	background-color: #e0e0e0;
	border-radius: 5px;
	overflow: hidden;
	margin: 20px 0;
}

.progress-bar .progress {
	height: 100%;
	background-color: #007bff;
	border-radius: 5px;
	transition: width 0.5s ease-in-out;
}

body.dark-mode .progress-bar {
	background-color: #555;
}

body.dark-mode .progress-bar .progress {
	background-color: #1e90ff;
}

.toggle-mode {
	position: fixed;
	top: 20px;
	right: 20px;
	background-color: #007bff;
	color: #fff;
	border: none;
	padding: 10px 20px;
	border-radius: 5px;
	cursor: pointer;
	transition: background-color 0.3s;
}

body.dark-mode .toggle-mode {
	background-color: #1e90ff;
}

.toggle-mode:hover {
	background-color: #0056b3;
}

body.dark-mode .toggle-mode:hover {
	background-color: #0077cc;
}

.icon {
	font-size: 2rem;
	margin-bottom: 10px;
	color: #007bff;
}

body.dark-mode .icon {
	color: #1e90ff;
}

script:
function toggleMode() {
	const body = document.body;
	body.classList.toggle("dark-mode");
}
