style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 2rem;
	padding: 20px;
	background: #fafafa;
	flex-wrap: wrap;
}

.card {
	width: 320px;
	padding: 1.5rem;
	border-radius: 24px;
	position: relative;
	transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.light-card {
	background: #ffffff;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05), 0 1px 4px rgba(0, 0, 0, 0.1);
}

.dark-card {
	background: #1a1f2c;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2), 0 1px 4px rgba(0, 0, 0, 0.15);
}

.card:hover {
	transform: translateY(-5px);
}

.card:hover .circular-progress {
	transform: scale(1.05);
}

.card-header {
	display: flex;
	align-items: flex-start;
	justify-content: space-between;
	margin-bottom: 2rem;
}

.header-text h2 {
	font-size: 1.25rem;
	font-weight: 600;
	margin-bottom: 0.5rem;
}

.light-card .header-text h2 {
	color: #1a1f2c;
}

.dark-card .header-text h2 {
	color: #ffffff;
}

.header-text p {
	font-size: 0.875rem;
	opacity: 0.7;
}

.light-card .header-text p {
	color: #64748b;
}

.dark-card .header-text p {
	color: #94a3b8;
}

.status-indicator {
	width: 40px;
	height: 40px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 1.25rem;
}

.light-card .status-indicator {
	background: #f1f5f9;
	color: #0ea5e9;
}

.dark-card .status-indicator {
	background: #334155;
	color: #38bdf8;
}

.progress-section {
	margin-bottom: 2rem;
}

.circular-progress {
	width: 150px;
	height: 150px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	position: relative;
	margin: 0 auto 2rem;
	transition: transform 0.3s ease;
}

.progress-ring {
	width: 100%;
	height: 100%;
	position: absolute;
	transform: rotate(-90deg);
}

.progress-ring circle {
	fill: none;
	stroke-width: 8;
	stroke-linecap: round;
	transition: stroke-dashoffset 1s ease;
}

.light-card .progress-ring .background {
	stroke: #f1f5f9;
}

.dark-card .progress-ring .background {
	stroke: #334155;
}

.light-card .progress-ring .progress {
	stroke: #0ea5e9;
}

.dark-card .progress-ring .progress {
	stroke: #38bdf8;
}

.progress-value {
	font-size: 2rem;
	font-weight: 700;
}

.light-card .progress-value {
	color: #1a1f2c;
}

.dark-card .progress-value {
	color: #ffffff;
}

.task-list {
	margin-bottom: 2rem;
}

.task-item {
	display: flex;
	align-items: center;
	padding: 0.75rem;
	border-radius: 12px;
	margin-bottom: 0.5rem;
	transition: all 0.3s ease;
}

.light-card .task-item {
	background: #f8fafc;
}

.dark-card .task-item {
	background: #242b38;
}

.task-item:hover {
	transform: translateX(5px);
}

.task-icon {
	width: 32px;
	height: 32px;
	border-radius: 8px;
	display: flex;
	align-items: center;
	justify-content: center;
	margin-right: 0.75rem;
}

.light-card .task-icon {
	background: #e0f2fe;
	color: #0ea5e9;
}

.dark-card .task-icon {
	background: #075985;
	color: #38bdf8;
}

.task-content {
	flex-grow: 1;
}

.task-title {
	font-size: 0.875rem;
	font-weight: 500;
	margin-bottom: 0.25rem;
}

.light-card .task-title {
	color: #1a1f2c;
}

.dark-card .task-title {
	color: #ffffff;
}

.task-subtitle {
	font-size: 0.75rem;
	opacity: 0.7;
}

.light-card .task-subtitle {
	color: #64748b;
}

.dark-card .task-subtitle {
	color: #94a3b8;
}

.task-progress {
	width: 60px;
	text-align: right;
	font-weight: 600;
}

.light-card .task-progress {
	color: #0ea5e9;
}

.dark-card .task-progress {
	color: #38bdf8;
}

.metrics {
	display: grid;
	grid-template-columns: repeat(3, 1fr);
	gap: 1rem;
}

.metric-item {
	text-align: center;
	padding: 1rem;
	border-radius: 16px;
	transition: transform 0.3s ease;
}

.light-card .metric-item {
	background: #f8fafc;
}

.dark-card .metric-item {
	background: #242b38;
}

.metric-item:hover {
	transform: translateY(-3px);
}

.metric-value {
	font-size: 1.25rem;
	font-weight: 700;
	margin-bottom: 0.25rem;
}

.light-card .metric-value {
	color: #1a1f2c;
}

.dark-card .metric-value {
	color: #ffffff;
}

.metric-label {
	font-size: 0.75rem;
	text-transform: uppercase;
	letter-spacing: 0.5px;
}

.light-card .metric-label {
	color: #64748b;
}

.dark-card .metric-label {
	color: #94a3b8;
}

@keyframes progress {
	from {
		stroke-dashoffset: var(--initial-offset);
	}
	to {
		stroke-dashoffset: var(--target-offset);
	}
}
