style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Plus Jakarta Sans", sans-serif;
}* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Plus Jakarta Sans', sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 2.5rem;
	padding: 20px;
	background: #f3f4f6;
	flex-wrap: wrap;
}



.progress-container {
	width: 320px;
	position: relative;
	overflow: hidden;
}

.card {
	padding: 2rem;
	border-radius: 20px;
	position: relative;
	transition: all 0.4s ease;
}

.light-theme {
	background: #ffffff;
	border: 1px solid #e5e7eb;
}

.dark-theme {
	background: #111827;
	border: 1px solid #374151;
}

.light-theme .priority-badge {
	background: #eff6ff;
	color: #3b82f6;
}

.dark-theme .priority-badge {
	background: #1f2937;
	color: #60a5fa;
}

.wave-progress {
	position: relative;
	height: 100px;
	margin: 2rem 0;
	overflow: hidden;
	border-radius: 10px;
}

.light-theme .wave-progress {
	background: #f3f4f6;
}

.dark-theme .wave-progress {
	background: #1f2937;
}

.wave {
	position: absolute;
	width: 100%;
	height: 100%;
	background: linear-gradient(90deg, #60a5fa, #34d399);
	animation: wave 8s linear infinite;
	opacity: 0.1;
}

.wave:nth-child(2) {
	animation-delay: -4s;
	opacity: 0.05;
}

@keyframes wave {
	0% {
		transform: translateX(-50%) scale(1);
	}
	50% {
		transform: translateX(0%) scale(0.8);
	}
	100% {
		transform: translateX(50%) scale(1);
	}
}

.progress-value {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	font-size: 2rem;
	font-weight: 700;
	z-index: 1;
}

.light-theme .progress-value {
	color: #111827;
}

.dark-theme .progress-value {
	color: #f9fafb;
}

.task-grid {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 1rem;
	margin: 2rem 0;
}

.task-item {
	padding: 1rem;
	border-radius: 12px;
	transition: all 0.3s ease;
}

.light-theme .task-item {
	background: #f9fafb;
}

.dark-theme .task-item {
	background: #1f2937;
}

.task-item:hover {
	transform: translateY(-5px);
}

.task-icon {
	margin-bottom: 0.5rem;
	font-size: 1.25rem;
}

.light-theme .task-icon {
	color: #3b82f6;
}

.dark-theme .task-icon {
	color: #60a5fa;
}

.task-label {
	font-size: 0.875rem;
	font-weight: 600;
	margin-bottom: 0.25rem;
}

.light-theme .task-label {
	color: #111827;
}

.dark-theme .task-label {
	color: #f9fafb;
}

.task-progress {
	font-size: 0.75rem;
}

.light-theme .task-progress {
	color: #6b7280;
}

.dark-theme .task-progress {
	color: #9ca3af;
}

.timeline {
	margin-top: 2rem;
}

.timeline-item {
	display: flex;
	align-items: flex-start;
	gap: 1rem;
	padding: 1rem 0;
	position: relative;
}

.timeline-dot {
	width: 12px;
	height: 12px;
	border-radius: 50%;
	margin-top: 0.25rem;
}

.light-theme .timeline-dot {
	background: #3b82f6;
	box-shadow: 0 0 0 4px #eff6ff;
}

.dark-theme .timeline-dot {
	background: #60a5fa;
	box-shadow: 0 0 0 4px #1f2937;
}

.timeline-content h3 {
	font-size: 0.875rem;
	font-weight: 600;
	margin-bottom: 0.25rem;
}

.light-theme .timeline-content h3 {
	color: #111827;
}

.dark-theme .timeline-content h3 {
	color: #f9fafb;
}

.timeline-content p {
	font-size: 0.75rem;
}

.light-theme .timeline-content p {
	color: #6b7280;
}

.dark-theme .timeline-content p {
	color: #9ca3af;
}

.timeline-item::after {
	content: "";
	position: absolute;
	left: 5px;
	top: 2rem;
	bottom: -1rem;
	width: 2px;
}

.light-theme .timeline-item::after {
	background: #e5e7eb;
}

.dark-theme .timeline-item::after {
	background: #374151;
}

.timeline-item:last-child::after {
	display: none;
}
