style:
/* Basic reset */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}
body {
	font-family: Arial, sans-serif;
	background: #f0f0f0;
	padding: 20px;
}
.container {
	display: flex;
	gap: 20px;
	flex-wrap: wrap;
	justify-content: center;
}
.card {
	width: 300px;
	border-radius: 10px;
	overflow: hidden;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease;
	cursor: pointer;
	background: #fff;
}
.card:hover {
	transform: translateY(-5px);
}
.card.light {
	background: #ffffff;
	color: #333;
}
.card.dark {
	background: #333;
	color: #f0f0f0;
}
.card-header {
	padding: 20px;
	display: flex;
	align-items: center;
	gap: 10px;
	border-bottom: 1px solid rgba(0, 0, 0, 0.1);
}
.card.dark .card-header {
	border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.card-title {
	font-size: 1.5em;
}
.card-body {
	padding: 20px;
}
.progress {
	width: 100%;
	height: 20px;
	background: rgba(0, 0, 0, 0.1);
	border-radius: 10px;
	overflow: hidden;
	margin-top: 10px;
}
.progress-bar {
	height: 100%;
	width: 0;
	background: #4caf50;
	border-radius: 10px;
	transition: width 1s ease;
}
.card.dark .progress {
	background: rgba(255, 255, 255, 0.1);
}
.card.dark .progress-bar {
	background: #81c784;
}
.progress-text {
	text-align: right;
	font-size: 0.9em;
	margin-top: 5px;
}

script:
window.addEventListener("load", function () {
	const cards = document.querySelectorAll(".card");
	cards.forEach((card) => {
		const progressValue = card.getAttribute("data-progress");
		const progressBar = card.querySelector(".progress-bar");
		setTimeout(() => {
			progressBar.style.width = progressValue + "%";
		}, 200);
	});
});
