style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}
body {
	font-family: "Arial", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: #f0f2f5;
}
.wrapper {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 20px;
	max-width: 900px;
	width: 100%;
}
.card {
	background-color: #fff;
	padding: 20px;
	border-radius: 15px;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	position: relative;
	overflow: hidden;
}
.card.dark {
	background-color: #2c2f33;
	color: white;
}
.icon-container {
	position: absolute;
	top: -30px;
	right: -30px;
	background-color: rgba(0, 0, 0, 0.05);
	padding: 20px;
	border-radius: 50%;
}
.icon {
	font-size: 4rem;
	color: #ff6b6b;
}
h2 {
	font-size: 1.8rem;
	margin: 20px 0 10px;
}
p {
	font-size: 0.95rem;
	color: #666;
}
.progress-bar-container {
	margin-top: 15px;
	background-color: #e0e0e0;
	height: 10px;
	border-radius: 8px;
}
.progress-bar {
	height: 10px;
	background: linear-gradient(90deg, #36d1dc, #5b86e5);
	border-radius: 8px;
	width: 75%;
}
