style:
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	font-family: "Inter", sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: var(--bg-color);
	transition: background-color 0.3s;
}

:root {
	--bg-color: #f0f0f0;
	--card-bg: #ffffff;
	--text-color: #333;
	--progress-color: #3498db;
}

body.dark-mode {
	--bg-color: #121212;
	--card-bg: #1f1f1f;
	--text-color: #e0e0e0;
	--progress-color: #4caf50;
}

.container {
	text-align: center;
}

.toggle-button {
	margin-bottom: 20px;
	padding: 8px 16px;
	cursor: pointer;
	background-color: var(--progress-color);
	color: white;
	border: none;
	border-radius: 5px;
}

.card-container {
	display: flex;
	gap: 20px;
	justify-content: center;
}

.card {
	width: 200px;
	background-color: var(--card-bg);
	color: var(--text-color);
	padding: 20px;
	border-radius: 10px;
	box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
	text-align: left;
}

.card-header {
	display: flex;
	align-items: center;
	justify-content: space-between;
	margin-bottom: 10px;
}

.progress-bar {
	background-color: var(--progress-color);
	height: 10px;
	border-radius: 5px;
	width: 70%;
}

.icon {
	font-size: 24px;
	color: var(--progress-color);
}

.progress-container {
	background-color: var(--bg-color);
	border: 1px solid var(--progress-color);
	border-radius: 5px;
	overflow: hidden;
}

.card-title {
	font-size: 18px;
	font-weight: bold;
}
