style:
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
}

body {
	font-family: "Arial", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: #ecf0f1;
}

.card-container {
	display: grid;
	grid-template-columns: 1fr 1fr;
	background: white;
	border-radius: 16px;
	box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
	max-width: 700px;
	overflow: hidden;
}

.card-image {
	background: url("https://picsum.photos/340/340?random=5") center/cover no-repeat;
	height: 100%;
	min-height: 300px;
}

.card-content {
	padding: 24px;
	display: flex;
	flex-direction: column;
	justify-content: center;
}

.card-title {
	font-size: 2rem;
	color: #2c3e50;
	margin-bottom: 10px;
}

.card-text {
	font-size: 1rem;
	color: #34495e;
	line-height: 1.6;
}

.button {
	display: inline-block;
	margin-top: 16px;
	padding: 12px 24px;
	background-color: #16a085;
	color: white;
	border: none;
	border-radius: 6px;
	text-decoration: none;
	font-size: 1rem;
	transition: background-color 0.3s;
	text-align: center;
}

.button:hover {
	background-color: #12876d;
}

@media (max-width: 600px) {
	.card-container {
		grid-template-columns: 1fr;
	}
	.card-image {
		height: 200px;
	}
}
