style:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Arial', sans-serif;
}

body {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background: #f0f2f5;
}

.split-card {
    width: 350px;
    background: white;
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 8px 24px rgba(0,0,0,0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    margin: 20px;
    position: relative;
}

.split-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 12px 28px rgba(0,0,0,0.15);
}

.card-image {
    width: 100%;
    height: 180px;
    object-fit: cover;
    transition: transform 0.4s ease;
}

.split-card:hover .card-image {
    transform: scale(1.05);
}

.card-content {
    padding: 20px;
    display: flex;
    flex-direction: column;
    gap: 16px;
}

.card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.card-title {
    color: #2c3e50;
    font-size: 1.4rem;
    font-weight: 600;
}

.card-price {
    background: #2ecc71;
    color: white;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 1rem;
    font-weight: 600;
}

.card-description {
    color: #7f8c8d;
    font-size: 0.95rem;
    line-height: 1.6;
}

.card-features {
    display: flex;
    flex-wrap: wrap;
    gap: 12px;
    margin-top: 10px;
}

.feature-item {
    display: flex;
    align-items: center;
    gap: 8px;
    background: #f8f9fa;
    padding: 8px 12px;
    border-radius: 20px;
    font-size: 0.9rem;
    color: #34495e;
}

.feature-icon {
    color: #2ecc71;
    font-size: 0.9rem;
}

.cta-button {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    background: #2ecc71;
    color: white;
    padding: 12px 24px;
    border: none;
    border-radius: 25px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    width: 100%;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.cta-button:hover {
    background: #27ae60;
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(46,204,113,0.3);
}

@media (max-width: 480px) {
    .split-card {
        width: 90%;
    }
}
