style:
.nature-card {
  max-width: 350px;
  background: white;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  margin: 20px;
}

.nature-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
}

.image-container {
  position: relative;
  width: 100%;
  height: 200px;
  overflow: hidden;
}

.image-container img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.3s ease;
}

.image-container:hover img {
  transform: scale(1.05);
}

.image-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.7));
  padding: 20px;
}

.content {
  padding: 20px;
}

.title {
  font-size: 1.5rem;
  font-weight: 700;
  color: #2c3e50;
  margin-bottom: 10px;
}

.description {
  color: #666;
  line-height: 1.6;
  margin-bottom: 20px;
}

.features {
  display: flex;
  gap: 15px;
  margin-bottom: 20px;
}

.feature-item {
  display: flex;
  align-items: center;
  gap: 5px;
}

.feature-icon {
  width: 30px;
  height: 30px;
  background: #e8f5e9;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #2e7d32;
}

.cta-button {
  display: inline-block;
  padding: 12px 24px;
  background: #2e7d32;
  color: white;
  text-decoration: none;
  border-radius: 25px;
  text-transform: uppercase;
  font-weight: 600;
  letter-spacing: 1px;
  transition: background 0.3s ease;
}

.cta-button:hover {
  background: #1b5e20;
}

@media (max-width: 768px) {
  .nature-card {
    width: 90%;
    margin: 20px auto;
  }

  .title {
    font-size: 1.3rem;
  }

  .features {
    flex-wrap: wrap;
  }
}
