style:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Arial", sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f0f9f0;
}

.card {
  display: flex;
  align-items: stretch;
  width: 600px;
  max-width: 90%;
  border-radius: 16px;
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  background: white;
  transition: transform 0.3s ease;
}

.card:hover {
  transform: translateY(-5px);
}

.card img {
  width: 50%;
  object-fit: cover;
  height: auto;
}

.card-content {
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  width: 50%;
  background-color: #ffffff;
}

.title {
  font-size: 1.8rem;
  font-weight: bold;
  color: #2d6932;
}

.description {
  font-size: 1rem;
  color: #444;
  margin: 10px 0;
}

.features {
  display: flex;
  gap: 10px;
  margin: 15px 0;
}

.feature-item {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: #2d6932;
  color: white;
  font-size: 1.4rem;
}

.button {
  text-transform: uppercase;
  background: #2d6932;
  color: white;
  padding: 10px 20px;
  border-radius: 24px;
  text-align: center;
  cursor: pointer;
  transition: background-color 0.3s;
}

.button:hover {
  background: #1c4a23;
}

@media (max-width: 768px) {
  .card {
    flex-direction: column;
  }

  .card img {
    width: 100%;
    height: 200px;
  }

  .card-content {
    width: 100%;
  }
}
