style:
@import url("https://fonts.googleapis.com/css2?family=Sora:wght@100..800&display=swap");

body {
  font-family: "Sora";
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  margin: 0;
  padding: 0;
  background: #fafaf9;
  gap: 2.5em;
}

.card {
  background: white;
  padding: 16px;
  border-radius: 16px;
  box-shadow: 0px 30px 100px 0px rgba(17, 23, 41, 0.1);
  width: min-content;
  margin: 0;
}

.card .img-container {
  width: 336px;
  height: 152px;
  overflow: hidden;
  display: flex;
  align-items: center;
  border-radius: 8px;
}

.card .tags {
  width: 336px;
  overflow: hidden;
  position: relative;
}

.card ul {
  list-style: none;
  padding: 0;
  margin: 16px 0;
  width: fit-content;
  display: flex;
  gap: 0.5em;
}

.card .tags::after {
  height: 100%;
  width: 25%;
  background-image: linear-gradient(-90deg, white 50%, transparent);
  content: "";
  position: absolute;
  right: -38px;
  top: 0;
  z-index: 5;
}

.card ul::-webkit-scrollbar {
  display: none;
}

.card ul li {
  padding: 4px 16px;
  background-color: rgba(58, 225, 155, 0.25);
  border-radius: 16px;
  width: fit-content;
  color: #04250c;
  font-size: 0.625rem;
  font-weight: 700;
}

.card h1 {
  font-size: 1.125rem;
  margin-bottom: 6px;
  font-weight: 700;
  color: #20293a;
}

.card p {
  font-size: 0.875rem;
  font-weight: 300;
  margin: 0;
  line-height: 1.5em;
  color: #6c727f;
  position: relative;
}

.card p:last-child {
  margin-top: 20px;
  padding-top: 20px;
  font-size: 0.75rem;
  font-weight: 400;
}

.card p:last-child::before {
  content: "";
  width: calc(100% + 32px);
  height: 1px;
  background: black;
  position: absolute;
  top: -1px;
  left: -16px;
  opacity: 0.1;
}

.card hr {
  opacity: 0.1;
  border-color: black;
  position: absolute;
}
