style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: #f0f0f0;
}

.card {
	max-width: 350px;
	background: white;
	border-radius: 15px;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	transition: transform 0.3s ease;
	margin: 20px;
}

.card:hover {
	transform: translateY(-10px);
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
}

.card-img {
	width: 100%;
	height: 250px;
	object-fit: cover;
	border-bottom: 4px solid #4caf50;
}

.card-content {
	padding: 25px;
}

.card-title {
	font-size: 1.5rem;
	color: #2c3e50;
	margin-bottom: 10px;
}

.card-subtitle {
	color: #4caf50;
	font-size: 0.9rem;
	text-transform: uppercase;
	letter-spacing: 1px;
	margin-bottom: 15px;
}

.card-text {
	color: #666;
	font-size: 0.95rem;
	line-height: 1.6;
	margin-bottom: 20px;
}

.card-button {
	display: inline-block;
	padding: 12px 30px;
	background-color: #4caf50;
	color: white;
	text-decoration: none;
	border-radius: 25px;
	font-weight: 600;
	transition: background-color 0.3s ease;
}

.card-button:hover {
	background-color: #45a049;
}
