style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Arial", sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: #e9f5ec;
}

.nature-card {
	width: 320px;
	background: #fff;
	border-radius: 12px;
	overflow: hidden;
	box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
	position: relative;
	transition: transform 0.3s;
	margin: 20px;
}

.nature-card:hover {
	transform: translateY(-8px);
}

.card-image {
	width: 100%;
	height: 220px;
	object-fit: cover;
	transition: transform 0.3s;
}

.nature-card:hover .card-image {
	transform: scale(1.05);
}

.card-content {
	padding: 20px;
	position: relative;
}

.card-tag {
	position: absolute;
	top: -20px;
	left: 20px;
	background: #2ecc71;
	color: white;
	padding: 6px 15px;
	border-radius: 20px;
	font-size: 0.85rem;
	font-weight: 600;
}

.card-title {
	font-size: 1.4rem;
	color: #2c3e50;
	margin-bottom: 10px;
}

.card-description {
	color: #7f8c8d;
	font-size: 0.95rem;
	line-height: 1.5;
	margin-bottom: 15px;
}

.card-stats {
	display: flex;
	justify-content: space-between;
	padding: 15px 0;
	border-top: 1px solid #ecf0f1;
}

.stat-item {
	text-align: center;
}

.stat-value {
	color: #2ecc71;
	font-weight: 700;
	font-size: 1.1rem;
}

.stat-label {
	color: #95a5a6;
	font-size: 0.8rem;
	text-transform: uppercase;
}

.image-overlay {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 220px;
	background: linear-gradient(to bottom, transparent 50%, rgba(0, 0, 0, 0.6));
}
