style:
/* Base Styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

body {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: #e9ecef;
}

/* Card Container */
.overlay-card {
  position: relative;
  width: 350px;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
  transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
}

.overlay-card:hover {
  transform: translateY(-8px);
}

/* Image Section */
.card-image-container {
  position: relative;
  height: 400px;
  overflow: hidden;
}

.card-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.4s ease;
}

.overlay-card:hover .card-image {
  transform: scale(1.05);
}

.gradient-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(180deg, rgba(0, 0, 0, 0) 40%, rgba(0, 0, 0, 0.8) 100%);
}

/* Content Overlay */
.card-content {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: 30px;
  color: white;
  z-index: 2;
}

.card-badge {
  position: absolute;
  top: 20px;
  right: 20px;
  background: rgba(255, 255, 255, 0.9);
  color: #2c3e50;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 0.85rem;
  font-weight: 600;
  backdrop-filter: blur(2px);
}

.card-title {
  font-size: 1.8rem;
  margin-bottom: 12px;
  line-height: 1.2;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.card-description {
  font-size: 1rem;
  line-height: 1.5;
  opacity: 0.9;
  margin-bottom: 20px;
  max-width: 80%;
}

.card-meta {
  display: flex;
  align-items: center;
  gap: 20px;
}

.rating {
  display: flex;
  align-items: center;
  gap: 5px;
  font-size: 1.1rem;
}

.rating-stars {
  color: #ffd700;
  letter-spacing: 2px;
}

.price {
  font-size: 1.4rem;
  font-weight: 700;
  color: #2ecc71;
}

/* Responsive Design */
@media (max-width: 480px) {
  .overlay-card {
    width: 90%;
    margin: 0 auto;
  }

  .card-content {
    padding: 20px;
  }

  .card-title {
    font-size: 1.5rem;
  }
}
