style:
/* Base Styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Arial", sans-serif;
}

body {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: #f4f7f6;
}

/* Card Container */
.feature-card {
  width: 320px;
  background: white;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  transition: transform 0.3s ease;
  margin: 20px;
}

.feature-card:hover {
  transform: translateY(-8px);
  box-shadow: 0 12px 28px rgba(0, 0, 0, 0.12);
}

/* Image Section */
.card-header {
  position: relative;
  height: 220px;
  overflow: hidden;
}

.card-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.4s ease;
}

.feature-card:hover .card-image {
  transform: scale(1.05);
}

/* Content Section */
.card-body {
  padding: 24px;
  position: relative;
}

.card-title {
  color: #2c3e50;
  font-size: 1.4rem;
  margin-bottom: 16px;
  font-weight: 600;
}

/* Features Grid */
.features-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
  margin-bottom: 24px;
}

.feature-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px;
  background: #f8f9fa;
  border-radius: 8px;
  transition: all 0.3s ease;
}

.feature-item:hover {
  background: #f1f3f5;
  transform: translateY(-2px);
}

.feature-icon {
  width: 36px;
  height: 36px;
  background: #2ecc71;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 1.1rem;
}

.feature-text {
  font-size: 0.95rem;
  color: #34495e;
  font-weight: 500;
}

/* Price Section */
.price-section {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 16px;
  border-top: 1px solid #eee;
}

.price-tag {
  font-size: 1.3rem;
  color: #2ecc71;
  font-weight: 700;
}

.book-button {
  background: #2ecc71;
  color: white;
  padding: 10px 24px;
  border: none;
  border-radius: 20px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
}

.book-button:hover {
  background: #27ae60;
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(46, 204, 113, 0.3);
}

/* Responsive Design */
@media (max-width: 480px) {
  .feature-card {
    width: 90%;
  }

  .features-grid {
    grid-template-columns: 1fr;
  }
}
