style:
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
}

body {
	font-family: "Arial", sans-serif;
	background-color: #f0f4fc;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
}

.testimonial-card {
	max-width: 350px;
	background-color: white;
	border-radius: 12px;
	padding: 20px;
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
	text-align: center;
	position: relative;
}

.testimonial-card::before {
	content: "★★★★★";
	color: #ffb400;
	font-size: 18px;
}

.testimonial-text {
	margin-top: 12px;
	font-size: 16px;
	color: #555;
}

.user-info {
	display: flex;
	align-items: center;
	gap: 12px;
	margin-top: 16px;
}

.user-image {
	width: 48px;
	height: 48px;
	border-radius: 50%;
	background-color: #ddd;
}

.user-details {
	text-align: left;
}

.user-name {
	font-weight: bold;
	color: #333;
}

.user-position {
	font-size: 14px;
	color: #777;
}

.testimonial-badge {
	display: inline-block;
	font-size: 12px;
	color: #0073ff;
	margin-top: 6px;
}
