style:
.testimonial-card.modern {
	background: linear-gradient(135deg, #6c5ce7, #4a90e2);
	color: white;
	padding: 2rem;
	border-radius: 20px;
	max-width: 320px;
}

.rating-stars {
	color: #ffd700;
	font-size: 1.2rem;
	margin-bottom: 1rem;
}

.avatar-initial {
	width: 50px;
	height: 50px;
	background: rgba(255, 255, 255, 0.2);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	font-weight: bold;
}
