style:
/* Reset default styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Arial", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: #f0f2f5;
	padding: 20px;
}

.testimonial-card {
	background: white;
	border-radius: 15px;
	padding: 30px;
	max-width: 400px;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
	position: relative;
	transition: transform 0.3s ease;
}

.testimonial-card:hover {
	transform: translateY(-5px);
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.quote-icon {
	position: absolute;
	top: 20px;
	right: 20px;
	color: rgba(0, 0, 0, 0.1);
	font-size: 2.5rem;
}

.user-image {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	object-fit: cover;
	margin-bottom: 20px;
	border: 3px solid #4a90e2;
}

.testimonial-text {
	color: #333;
	font-size: 1rem;
	line-height: 1.6;
	margin-bottom: 20px;
}

.user-info {
	display: flex;
	align-items: center;
	gap: 15px;
}

.user-details h3 {
	color: #2c3e50;
	font-size: 1.1rem;
	margin-bottom: 5px;
}

.user-details p {
	color: #7f8c8d;
	font-size: 0.9rem;
}

.rating {
	color: #f1c40f;
	margin-bottom: 15px;
}

.rating i {
	margin-right: 3px;
}

@media (max-width: 480px) {
	.testimonial-card {
		padding: 20px;
	}

	.testimonial-text {
		font-size: 0.9rem;
	}
}
