style:
body {
	background: #f3f4f6;
	font-family: "Roboto", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
}

.testimonial-card {
	background: #ffffff;
	padding: 2.5rem;
	max-width: 380px;
	width: 90%;
	border-radius: 15px;
	box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
	position: relative;
	text-align: center;
}

.testimonial-card .header {
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 0.3rem;
}

.testimonial-card .header .stars {
	color: #f59e0b;
	font-size: 1.5rem;
}

.testimonial-card p {
	font-size: 1rem;
	color: #374151;
	line-height: 1.6;
	margin: 1.5rem 0;
	font-style: italic;
}

.testimonial-card .user {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
}

.testimonial-card .user img {
	width: 70px;
	height: 70px;
	border-radius: 50%;
	object-fit: cover;
	border: 2px solid #f59e0b;
}

.testimonial-card .user-info h3 {
	margin: 0;
	font-size: 1.3rem;
	color: #111827;
}

.testimonial-card .user-info span {
	font-size: 0.9rem;
	color: #6b7280;
}
