style:
body {
	background: linear-gradient(135deg, #f3f4f6, #e5e7eb);
	font-family: "Roboto", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
}

.testimonial-card {
	background: #ffffff;
	padding: 2.5rem;
	max-width: 400px;
	width: 90%;
	border-radius: 20px;
	box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);
	position: relative;
	text-align: center;
	overflow: hidden;
}

.testimonial-card::before {
	content: "";
	position: absolute;
	top: -50px;
	right: -50px;
	width: 140px;
	height: 140px;
	background: #f59e0b;
	opacity: 0.2;
	transform: rotate(45deg);
	border-radius: 50%;
}

.testimonial-card .header {
	display: flex;
	justify-content: center;
	align-items: center;
	gap: 0.3rem;
	margin-bottom: 1rem;
}

.testimonial-card .header .stars {
	color: #f59e0b;
	font-size: 1.5rem;
}

.testimonial-card p {
	font-size: 1.1rem;
	color: #374151;
	line-height: 1.8;
	margin: 1.5rem 0;
	font-style: italic;
}

.testimonial-card .user {
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
	margin-top: 2rem;
}

.testimonial-card .user img {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	object-fit: cover;
	border: 3px solid #f59e0b;
	box-shadow: 0 6px 12px rgba(0, 0, 0, 0.1);
}

.testimonial-card .user-info h3 {
	margin: 0;
	font-size: 1.4rem;
	color: #111827;
}

.testimonial-card .user-info span {
	font-size: 0.9rem;
	color: #6b7280;
}
