style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: #f0f2f5;
	padding: 2rem;
}

.testimonial-wrapper {
	perspective: 1000px;
	width: 320px;
	height: 450px;
}

.testimonial-card {
	width: 100%;
	height: 100%;
	position: relative;
	transform-style: preserve-3d;
	transition: transform 0.8s;
	cursor: pointer;
}

.testimonial-card:hover {
	transform: rotateY(180deg);
}

.card-face {
	position: absolute;
	width: 100%;
	height: 100%;
	backface-visibility: hidden;
	border-radius: 20px;
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
	padding: 2rem;
	background: white;
	display: flex;
	flex-direction: column;
	align-items: center;
}

.back {
	transform: rotateY(180deg);
}

.user-avatar {
	width: 100px;
	height: 100px;
	border-radius: 50%;
	object-fit: cover;
	margin-bottom: 1.5rem;
	border: 3px solid #4a90e2;
	box-shadow: 0 5px 15px rgba(74, 144, 226, 0.2);
}

.user-name {
	color: #2c3e50;
	font-size: 1.4rem;
	margin-bottom: 0.5rem;
}

.user-role {
	color: #7f8c8d;
	font-size: 0.95rem;
	margin-bottom: 1.5rem;
}

.testimonial-text {
	color: #34495e;
	text-align: center;
	line-height: 1.6;
	margin-bottom: 2rem;
}

.stats-grid {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 1rem;
	width: 100%;
}

.stat-item {
	text-align: center;
	padding: 1rem;
	background: rgba(74, 144, 226, 0.08);
	border-radius: 10px;
}

.stat-number {
	color: #4a90e2;
	font-size: 1.3rem;
	font-weight: 600;
}

.stat-label {
	color: #7f8c8d;
	font-size: 0.85rem;
}

.social-proof {
	display: flex;
	gap: 1rem;
	margin-top: auto;
}

.badge {
	padding: 0.5rem 1rem;
	border-radius: 20px;
	font-size: 0.8rem;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

.google-badge {
	background: rgba(66, 133, 244, 0.1);
	color: #4285f4;
}

.trustpilot-badge {
	background: rgba(0, 168, 84, 0.1);
	color: #00a854;
}

.quote-icon {
	position: absolute;
	font-size: 6rem;
	opacity: 0.05;
	top: 20px;
}

@media (max-width: 480px) {
	.testimonial-wrapper {
		width: 100%;
		height: auto;
	}

	.testimonial-card {
		height: auto;
		position: relative;
		transform: none;
	}

	.card-face {
		position: relative;
		transform: none;
		backface-visibility: visible;
	}

	.testimonial-card:hover {
		transform: none;
	}
}
