style:
body {
	font-family: "Inter", sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: #f8f9fa;
	margin: 0;
	padding: 20px;
}

.testimonial-card {
	background: white;
	border-radius: 16px;
	padding: 2.5rem;
	max-width: 400px;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.04);
	transition: transform 0.3s ease;
}

.testimonial-card:hover {
	transform: translateY(-5px);
}

.quote-icon {
	color: #e0e0e0;
	font-size: 2.5rem;
	margin-bottom: 1.5rem;
}

.testimonial-text {
	color: #444;
	font-size: 1rem;
	line-height: 1.7;
	margin-bottom: 1.5rem;
}

.user-info {
	display: flex;
	align-items: center;
	gap: 1rem;
}

.user-avatar {
	width: 56px;
	height: 56px;
	border-radius: 50%;
	object-fit: cover;
}

.user-details h3 {
	margin: 0;
	color: #222;
	font-size: 1.1rem;
}

.user-details p {
	margin: 0;
	color: #666;
	font-size: 0.9rem;
}

.rating {
	color: #ffd700;
	margin-top: 1rem;
}
