style:
/* Basic reset and body styling */
body {
	background: #f5f5f5;
	font-family: Arial, sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	margin: 0;
}

/* Testimonial Card Styling */
.testimonial-card {
	background: #fff;
	padding: 1.5rem;
	max-width: 350px;
	width: 90%;
	border-radius: 15px;
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
	text-align: left;
}

/* Top bar with star rating */
.testimonial-card .top-bar {
	display: flex;
	align-items: center;
	justify-content: space-between;
}

.testimonial-card .top-bar .stars {
	color: #f39c12;
	font-size: 1.2rem;
}

.testimonial-card .top-bar .date {
	font-size: 0.85rem;
	color: #888;
}

/* Testimonial text */
.testimonial-card p {
	font-size: 1rem;
	color: #333;
	line-height: 1.6;
	margin: 1rem 0;
}

/* User info container */
.testimonial-card .user {
	display: flex;
	align-items: center;
	gap: 1rem;
}

/* User image */
.testimonial-card .user img {
	width: 60px;
	height: 60px;
	border-radius: 50%;
	object-fit: cover;
	border: 2px solid #f39c12;
}

/* User name */
.testimonial-card .user .info h3 {
	margin: 0;
	font-size: 1rem;
	color: #333;
}

/* User role or additional info */
.testimonial-card .user .info span {
	font-size: 0.85rem;
	color: #777;
}
