style:
/* Base Styles */
body {
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: #f8f9fa;
	margin: 0;
	padding: 20px;
}

.testimonial-card {
	background: linear-gradient(135deg, #ffffff, #f8f9ff);
	border-radius: 20px;
	padding: 30px;
	max-width: 450px;
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.08);
	position: relative;
	transition: transform 0.3s ease;
	overflow: hidden;
}

.testimonial-card:hover {
	transform: translateY(-10px);
}

.card-header {
	display: flex;
	align-items: center;
	margin-bottom: 25px;
	position: relative;
}

.user-avatar {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	object-fit: cover;
	border: 3px solid #4a90e2;
	margin-right: 20px;
	box-shadow: 0 5px 15px rgba(74, 144, 226, 0.2);
}

.user-info {
	flex: 1;
}

.user-name {
	color: #2c3e50;
	font-size: 1.4rem;
	margin-bottom: 5px;
}

.user-role {
	color: #7f8c8d;
	font-size: 0.95rem;
}

.quote-container {
	position: relative;
	margin-bottom: 25px;
}

.quote-icon {
	color: #4a90e2;
	font-size: 3rem;
	position: absolute;
	opacity: 0.1;
}

.quote-icon.left {
	top: -15px;
	left: -10px;
}

.quote-icon.right {
	bottom: -25px;
	right: -10px;
	transform: rotate(180deg);
}

.testimonial-text {
	color: #34495e;
	font-size: 1.05rem;
	line-height: 1.7;
	position: relative;
	z-index: 1;
	padding: 0 20px;
}

.stats-container {
	display: flex;
	justify-content: space-between;
	margin-top: 25px;
	padding-top: 25px;
	border-top: 1px solid #eee;
}

.stat-item {
	text-align: center;
}

.stat-number {
	color: #4a90e2;
	font-size: 1.4rem;
	font-weight: 600;
	margin-bottom: 5px;
}

.stat-label {
	color: #7f8c8d;
	font-size: 0.9rem;
}

.social-links {
	position: absolute;
	top: 20px;
	right: 20px;
	display: flex;
	gap: 10px;
}

.social-links a {
	color: #7f8c8d;
	transition: color 0.3s ease;
	text-decoration: none;
}

.social-links a:hover {
	color: #4a90e2;
}

.verified-badge {
	position: absolute;
	bottom: -3px;
	right: -3px;
	background: #4a90e2;
	color: white;
	width: 25px;
	height: 25px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 0.8rem;
	border: 2px solid white;
}

@media (max-width: 480px) {
	.testimonial-card {
		padding: 20px;
	}

	.card-header {
		flex-direction: column;
		text-align: center;
	}

	.user-avatar {
		margin-right: 0;
		margin-bottom: 15px;
	}

	.social-links {
		position: static;
		justify-content: center;
		margin-top: 15px;
	}
}
